mapper.select:
select count(DISTINCT jss.id) from job_signal_status jss
left join job_signal_status_header jssh on jss.id = jssh.signalStatusId


        and jssh.device_id = #{deviceId}


        and jss.create_time between #{startDate} and #{endDate}

select distinct jss.id from job_signal_status jss
left join job_signal_status_header jssh on jss.id = jssh.signalStatusId



        and jssh.device_id = #{deviceId}


        and jss.create_time between #{startDate} and #{endDate}



order by jss.create_time desc
limit #{startIndex}, #{pageSize}

SELECT jssdp.job_id as jobId FROM job_signal_status_device_phase jssdp left join job_signal_status_device jssd on jssdp.signalStatusDeviceId=jssd.id left join job_signal_status jss on jssd.signalStatusId=jss.id left join job_signal_status_header jssh on jss.id=jssh.signalStatusId
and jssh.device_id = #{deviceId}
and jss.create_time between #{startDate} and #{endDate}
GROUP BY jssdp.job_id order by jss.create_time desc
